    private void jBtn_ingresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtn_ingresarActionPerformed
        // TODO add your handling code here:
        try {
            if (jTF_username.getText().isEmpty() || jPF_password.getText().isEmpty()) {
                JOptionPane.showMessageDialog(this, "Llene las casillas.");
            } else {
                AdminUser au = new AdminUser("./datos.txt");
                au.cargarArchivo();

                boolean usuarioValido = false;

                for (user usuario : au.getListaUser()) {
                    if (usuario.getUsername().equals(jTF_username.getText()) && usuario.getPassword().equals(jPF_password.getText())) {
                        usuarioValido = true;
                        JOptionPane.showMessageDialog(this, "Bienvenido!!");
                        jP_login.setVisible(false);
                        jP_MenuPrincipal.setVisible(true);
                        usuarioEnSesion=jTF_username.getText();
                        TipoUsuarioEnSesion = usuario.getTipoUsuario().toString();
                    }
                }

                if (!usuarioValido && jTF_username.getText().equalsIgnoreCase("admin") && jPF_password.getText().equals("supersecreto")) {
                    usuarioValido = true;
                    JOptionPane.showMessageDialog(this, "BIENVENIDO");
                    jP_login.setVisible(false);
                    jP_MenuPrincipal.setVisible(true);
                    usuarioEnSesion=jTF_username.getText();
                    TipoUsuarioEnSesion = "ADMINISTRADOR";
                }

                if (!usuarioValido) {
                    JOptionPane.showMessageDialog(this, "Usuario o contraseña incorrectos.");
                }
                Usuario.setText("Usuario: "+usuarioEnSesion);
                TipoUsuario.setText("Tipo de Usuario: "+TipoUsuarioEnSesion);
            }
        } catch (Exception e) {
            e.printStackTrace();
            JOptionPane.showMessageDialog(this, "Error al intentar ingresar: " + e.getMessage());
        }
    }//GEN-LAST:event_jBtn_ingresarActionPerformed

