    private void jButton8ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton8ActionPerformed
        // TODO add your handling code here:
        try {
            if (camposValidos()) {
                int edad= (int)jS_edad.getComponentCount();
                user p = new user(jTF_nombre.getText(), jTF_usernamee1.getText(), jTF_contra.getText(), edad) {
                    String tipoUsuario = (String) jComboBox1.getSelectedItem();

                    @Override
                    public TipoUser getTipoUsuario() {
                        switch (tipoUsuario) {
                            case "ADMINISTRADOR":
                                return TipoUser.ADMINISTRADOR;
                            case "LIMITADO":
                                return TipoUser.LIMITADO;
                            case "CONTENIDO":
                                return TipoUser.CONTENIDOS;
                            default:
                                return TipoUser.ADMINISTRADOR;
                        }
                    }
                };
                AdminUser adminUser = new AdminUser("./datos.txt");
                adminUser.cargarArchivo();
                adminUser.setUser(p);
                adminUser.escribirArchivo();
                
                JOptionPane.showMessageDialog(this, "Usuario creado exitosamente");
            } else {
                JOptionPane.showMessageDialog(this, "Complete todos los campos antes de crear un usuario");
            }
        } catch (Exception e) {
            e.printStackTrace();
            JOptionPane.showMessageDialog(this, "Error al crear el usuario");
        }
    }//GEN-LAST:event_jButton8ActionPerformed

